/**
 * @description
 * 控制器基类
 */
class Controller {
  constructor() {}

  download(res, file) {
    res.download(file);
  }

  success(res, data = null) {
    res.json({ code: 200, msg: "success", data: data });
  }

  fail(res, msg = "操作失败，请稍后再试") {
    res.json({ code: 400, msg: msg });
  }

  error(res, msg) {
    res.json({ code: 500, msg: msg });
  }
}

module.exports = Controller;
